{
  int n;
  VERTEX *v;
  for (n=0; n < mris->nvertices; n++)
  {
    v=&mris->vertices[n];
    if (v->ripflag) continue;
    v->curv = ((VALS_VP*)v->vp)->orig_vals[fno];
  }
}

