/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
int
MRISwritePatch(MRI_SURFACE *mris, const char *fname)
{
  int    k,i,npts, type ;
  float  x,y,z;
  FILE   *fp;

  type = MRISfileNameType(fname) ;
  if (type == MRIS_ASCII_TRIANGLE_FILE) // extension is ASC
    return(MRISwritePatchAscii(mris, fname)) ;
  else if (type == MRIS_GEO_TRIANGLE_FILE) // extension is GEO
    return(MRISwriteGeo(mris, fname)) ;

  // binary file write
  // count number of points
  npts = 0;
  for (k=0;k<mris->nvertices;k++)
    if (!mris->vertices[k].ripflag) npts++;
  if (Gdiag & DIAG_SHOW && DIAG_VERBOSE_ON)
    fprintf(stdout, "writing surface patch with npts=%d to %s\n",npts,fname);
  // binary write
  fp = fopen(fname,"wb");
  if (fp==NULL)
    ErrorReturn(ERROR_NOFILE,
                (ERROR_NOFILE,
                 "MRISwritePatch: can't create file %s\n",fname)) ;
  // write num points
  fwriteInt(-1, fp) ;  // "version" #
  fwriteInt(npts, fp) ;
  // go through all points
  for (k=0;k<mris->nvertices;k++)
    if (!mris->vertices[k].ripflag)
    {
      i = (mris->vertices[k].border)? (-(k+1)): (k+1);
      fwriteInt(i, fp) ;
      x = mris->vertices[k].x;
      y = mris->vertices[k].y;
      z = mris->vertices[k].z;
      fwriteFloat(x,fp);
      fwriteFloat(y,fp);
      fwriteFloat(z,fp);
      /*
        printf("k=%d, i=%d\n",k,i);
      */
    }
  fclose(fp);
  return(NO_ERROR) ;
}
/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
int
MRISsmoothSurfaceNormals(MRI_SURFACE *mris, int navgs)
{
  int    i, vno, vnb, *pnb, vnum ;
  float  nx, ny, nz, num, len ;
  VERTEX *v, *vn ;

  for (i = 0 ; i < navgs ; i++)
  {
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      v = &mris->vertices[vno] ;
      if (v->ripflag)
        continue ;
      nx = v->nx ;
      ny = v->ny ;
      nz = v->nz ;
      pnb = v->v ;
      vnum = v->vnum ;
      for (num = 0.0f, vnb = 0 ; vnb < vnum ; vnb++)
      {
        vn = &mris->vertices[*pnb++] ; /* neighboring vertex pointer */
        if (vn->ripflag)
          continue ;
        num++ ;
        nx += vn->nx ;
        ny += vn->ny ;
        nz += vn->nz ;
      }
      num++ ;   /* account for central vertex */
      v->tdx = nx / num ;
      v->tdy = ny / num ;
      v->tdz = nz / num ;
    }
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      v = &mris->vertices[vno] ;
      if (v->ripflag)
        continue ;
      len = sqrt(v->tdx*v->tdx+v->tdy*v->tdy+v->tdz*v->tdz) ;
      if (FZERO(len))
        len = 1 ;
      v->nx = v->tdx/len ;
      v->ny = v->tdy/len ;
      v->nz = v->tdz/len ;
    }
  }

  return(NO_ERROR) ;
}
