/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  Compute the folding (FI) and intrinsic curvature (ICI)
  indices of the surface.

  Drury and Van Essen:  ICI = 56  (lh) and 54  (rh)
  FI = 500 (lh) and 520 (rh)
  ------------------------------------------------------*/
int
MRIScomputeCurvatureIndices(MRI_SURFACE *mris, double *pici, double *pfi)
{
  int    vno ;
  VERTEX *vertex ;
  double k1, k2, ici, fi, area, Kmax, Kmin ;

  ici = fi = 0.0 ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    vertex = &mris->vertices[vno] ;
    if (vertex->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;
    k1 = (double)vertex->k1 ;
    k2 = (double)vertex->k2 ;
    area = (double)vertex->area ;
    if (vertex->K > 0)
      ici += area * (double)vertex->K ;
    Kmax = (double)fabs(k1) ;
    Kmin = (double)fabs(k2) ;
    fi += area * Kmax * (Kmax - Kmin) ;
  }

  *pfi = fi / (4.0*M_PI) ;
  *pici = ici / (4.0 * M_PI) ;
  return(NO_ERROR) ;
}
/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
double
MRISmaxRadius(MRI_SURFACE *mris)
{
  double  radius ;
  int    vno, n ;
  VERTEX *vertex ;
  double x, y, z, xlo, ylo, zlo, xhi, yhi, zhi, x0, y0, z0, r ;

  xhi=yhi=zhi= -10000;
  xlo=ylo=zlo= 10000;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    vertex = &mris->vertices[vno] ;
#if 0
    if (vertex->ripflag)
      continue ;
#endif
    x = (double)vertex->x ;
    y = (double)vertex->y ;
    z = (double)vertex->z ;
    if (x>xhi) xhi=x;
    if (x<xlo) xlo=x;
    if (y>yhi) yhi=y;
    if (y<ylo) ylo=y;
    if (z>zhi) zhi=z;
    if (z<zlo) zlo=z;
  }
  mris->xhi = xhi ;
  mris->xlo = xlo ;
  mris->yhi = yhi ;
  mris->ylo = ylo ;
  mris->zhi = zhi ;
  mris->zlo = zlo ;
  x0 = (xlo+xhi)/2.0f ;
  y0 = (ylo+yhi)/2.0f ;
  z0 = (zlo+zhi)/2.0f ;
  for (radius = 0.0, n = vno = 0 ; vno < mris->nvertices ; vno++)
  {
    vertex = &mris->vertices[vno] ;
#if 0
    if (vertex->ripflag)
      continue ;
#endif
    n++ ;
    x = (double)vertex->x-x0 ;
    y = (double)vertex->y-y0 ;
    z = (double)vertex->z-z0 ;
    r = sqrt(x*x + y*y + z*z) ;
    if (r > radius)
      radius = r ;
  }

  return(radius) ;
}
