/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
double
MRIStotalVariationDifference(MRI_SURFACE *mris)
{
  int     vno, nvertices ;
  VERTEX  *v ;
  double  var, delta ;

  nvertices = mris->nvertices ;
  for (var = 0.0, vno = 0 ; vno < nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    delta = fabs(v->k1 - v->k2) ;
    delta *= delta ;
    var += v->area * delta ;
    if (!finite(var))
      ErrorPrintf(ERROR_BADPARM, "curvature at vertex %d is not finite!\n",
                  vno) ;
  }
  return(var) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
double
MRIStotalVariation(MRI_SURFACE *mris)
{
  int     vno, nvertices ;
  VERTEX  *v ;
  double  var, delta ;

  nvertices = mris->nvertices ;
  for (var = 0.0, vno = 0 ; vno < nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    delta = v->k1 * v->k1 + v->k2 * v->k2 ;
    var += v->area * delta ;
    if (!finite(var))
      ErrorPrintf(ERROR_BADPARM, "curvature at vertex %d is not finite!\n",
                  vno) ;
  }
  return(var) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
double
MRIScurvatureError(MRI_SURFACE *mris, double Kd)
{
  int     vno, nvertices, n ;
  VERTEX  *v ;
  double  Kerror, deltaK ;

  nvertices = mris->nvertices ;
  for (Kerror = 0.0, n = vno = 0 ; vno < nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    deltaK = (v->k1-Kd) ;
    deltaK *= deltaK ;
    Kerror += v->area * deltaK ;
    n++;
    deltaK = (v->k2-Kd) ;
    deltaK *= deltaK ;
    Kerror += v->area * deltaK ;
    n++ ;
  }
  /*  return(sqrt(Kerror/(double)n)) ;*/
  return(Kerror) ;
}


