/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
static int
mrisSmoothNormals(MRI_SURFACE *mris, int niterations)
{
  int     vno, i, vn ;
  double  g ;
  VERTEX  *vertex, *vnb ;
  VECTOR  *v_n, *v_n2 ;

  v_n = VectorAlloc(3, MATRIX_REAL) ;       /* normal vector */
  v_n2 = VectorAlloc(3, MATRIX_REAL) ;       /* normal vector */

  for (i = 0 ; i < niterations ; i++)
  {
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      vertex = &mris->vertices[vno] ;
      if (vertex->ripflag)
        continue ;
      VECTOR_LOAD(v_n, vertex->nx, vertex->ny, vertex->nz) ;
      V3_SCALAR_MUL(v_n, kernel[0], v_n) ;
      g = kernel[1] ;
      for (vn = 0 ; vn < vertex->vnum ; vn++)
      {
        vnb = &mris->vertices[vertex->v[vn]] ;
        VECTOR_LOAD(v_n2, vnb->nx, vnb->ny, vnb->nz) ;
        V3_SCALAR_MUL(v_n2, g, v_n2) ;
        V3_ADD(v_n, v_n2, v_n) ;
      }
      g = kernel[2] ;
      for ( ; vn < vertex->v2num ; vn++)
      {
        vnb = &mris->vertices[vertex->v[vn]] ;
        VECTOR_LOAD(v_n2, vnb->nx, vnb->ny, vnb->nz) ;
        V3_SCALAR_MUL(v_n2, g, v_n2) ;
        V3_ADD(v_n, v_n2, v_n) ;
      }
      V3_NORMALIZE(v_n, v_n) ;
      vertex->tdx = V3_X(v_n) ;
      vertex->tdy = V3_Y(v_n) ;
      vertex->tdz = V3_Z(v_n) ;
    }
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      vertex = &mris->vertices[vno] ;
      if (vertex->ripflag)
        continue ;
      vertex->nx = vertex->tdx ;
      vertex->ny = vertex->tdy ;
      vertex->nz = vertex->tdz ;
    }
  }

  VectorFree(&v_n) ;
  VectorFree(&v_n2) ;
  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
static int
mrisSmoothNormalOutliers(MRI_SURFACE *mris, double ndist)
{
  int     vno, n, m, smooth, nsmoothed ;
  VERTEX  *v, *vn ;
  float   sx, sy, sz, nx, ny, nz, nc, x, y, z, dist, dx, dy, dz ;


  for (nsmoothed = vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    nx = v->nx ;
    ny = v->ny ;
    nz = v->nz ;
    x = v->x ;
    y = v->y ;
    z = v->z ;

    sx = sy = sz = 0.0 ;
    n=0;
    /* see if we are futher than ndist from all our neighbors in
       the normal direction. If so, smooth.
    */
    for (smooth = 1, m = 0 ; smooth && m < v->vnum ; m++)
    {
      vn = &mris->vertices[v->v[m]] ;
      if (!vn->ripflag)
      {
        dx = vn->x - x;
        dy = vn->y - y;
        dz = vn->z - z;
        nc = dx*nx+dy*ny+dz*nz;   /* projection onto normal */
        dist = sqrt(nc) ;         /* distance in normal direction */
        if (dist < ndist)
          smooth = 0 ;
        sx += dx ;
        sy += dy ;
        sz += dz ;
        n++;
      }
    }
    if (!smooth)
      continue ;
    nsmoothed++ ;
    if (n>0)
    {
      sx = sx/n;
      sy = sy/n;
      sz = sz/n;
    }
#if 0
    nc = sx*nx+sy*ny+sz*nz;   /* projection onto normal */
    sx = nc*nx ;              /* move in normal direction */
    sy = nc*ny ;
    sz = nc*nz;
#endif

    v->dx += sx ;
    v->dy += sy ;
    v->dz += sz ;
  }

  fprintf(stdout, "%d smoothed (%2.2f%%)\n",
          nsmoothed, 100.0f*(float)nsmoothed / (float)mris->nvertices) ;
  return(NO_ERROR) ;
}


static int
mrisComputeAverageNormalTerm(MRI_SURFACE *mris, int navgs, double l_normal)
{
  VERTEX  *v, *vn ;
  double  nc_avg, nc, vnum, delta ;
  int     n, vno, marked ;
  float   x, y, z, dx, dy, dz, nx, ny, nz, s ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;
    marked = v->marked ;

    /* compute projection onto normal */
    x = v->x ;
    y = v->y ;
    z = v->z ;
    nx = v->nx ;
    ny = v->ny ;
    nz = v->nz ;
    for (vnum = nc_avg = 0.0, n = 0 ; n < v->vnum ; n++)
    {
      vn = &mris->vertices[v->v[n]] ;
      if (vn->marked != marked)
        continue ;
      vnum++ ;
      dx = vn->x - x ;
      dy = vn->y - y ;
      dz = vn->z - z ;
      nc_avg += dx*nx+dy*ny+dz*nz;   /* projection onto normal */
    }
    if (vnum > 0.0)
      nc_avg /= vnum ;
    else
      nc_avg = 0.0 ;
    v->d = nc_avg ;
  }

  mrisAverageDs(mris, navgs) ;

  /* now move each vertex in the direction of the local average */
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;
    marked = v->marked ;

    /* compute projection onto normal */
    nc_avg = v->d ;
    x = v->x ;
    y = v->y ;
    z = v->z ;
    nx = v->nx ;
    ny = v->ny ;
    nz = v->nz ;
    for (nc = vnum = 0.0, n = 0 ; n < v->vnum ; n++)
    {
      vn = &mris->vertices[v->v[n]] ;
      if (vn->marked != marked)
        continue ;
      vnum++ ;
      dx = vn->x - x ;
      dy = vn->y - y ;
      dz = vn->z - z ;
      nc += dx*nx+dy*ny+dz*nz;   /* projection onto normal */
    }
    if (vnum > 0.0)
      nc /= (float)vnum ;
    else
      nc = 0.0 ;

    s = nc_avg < 0.0 ? -1.0 : 1.0 ;
    nc_avg = sqrt(fabs(nc_avg)) * s ;
    s = nc < 0.0 ? -1.0 : 1.0 ;
    nc = sqrt(fabs(nc)) * s ;
    delta = -l_normal * (nc_avg - nc) ;
    dx = nx * delta ;
    dy = ny * delta ;
    dz = nz * delta ;
    v->dx += dx ;
    v->dy += dy ;
    v->dz += dz ;
  }
  return(NO_ERROR) ;
}
static int
mrisComputeCurvatureTerm(MRI_SURFACE *mris, double l_curv)
{
  int     vno, n ;
  VERTEX  *v, *vn ;
  float   nc, nx, ny, nz, x, y, z, ndx, ndy, ndz, tdx, tdy, tdz, dx, dy, dz,
  tdist, ndist, nc_avg ;
  double  curv ;

  if (FZERO(l_curv))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    nx = v->nx ;
    ny = v->ny ;
    nz = v->nz ;
    x = v->x ;
    y = v->y ;
    z = v->z ;

    /* first mean tangential and normal spacing */
    nc_avg = ndx = ndy = ndz = tdist = ndist = 0.0f ;
    for (n = 0 ; n < v->vnum ; n++)
    {
      vn = &mris->vertices[v->v[n]] ;
      dx = vn->x - x ;
      dy = vn->y - y ;
      dz = vn->z - z ;
      nc = (dx*nx + dy*ny + dz*nz) ;
      nc_avg += nc ;
#if 0
      ndx += nc*nx ;
      ndy += nc*ny ;
      ndz += nc*nz ;
#endif
      tdx = dx-nc*nx ;
      tdy = dy-nc*ny ;
      tdz = dz-nc*nz ;
      tdist += sqrt(tdx*tdx+tdy*tdy+tdz*tdz) ;
    }
#if 0
    ndx /= (float)v->vnum ;
    ndy /= (float)v->vnum ;
    ndz /= (float)v->vnum ;
    ndist = sqrt(ndx*ndx+ndy*ndy+ndz*ndz) ;
    if (nc_avg < 0.0f)
      ndist *= -1 ;   /* neighbors are predominantly below tangent plane */
#else
    ndist = nc_avg ;
#endif
    tdist /= (float)v->vnum ;
    ndist /= (float)v->vnum ;
    if (FZERO(tdist))
      continue ;
    curv = ndist / tdist ;

    if (fabs(curv) < 0.25f)
      continue ;
    curv *= l_curv ;
    v->dx += curv * nx ;
    v->dy += curv * ny ;
    v->dz += curv * nz ;
    if (vno == Gdiag_no)
      fprintf(stdout,
              "vertex %d normal curvature term: (%2.3f, %2.3f, %2.3f)\n"
              , vno, curv*nx, curv*ny, curv*nz) ;
  }

  return(NO_ERROR) ;
}
