/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
static int
mrisComputeConvexityTerm(MRI_SURFACE *mris, double l_convex)
{
  int     vno, n, m ;
  VERTEX  *vertex, *vn ;
  float   sx, sy, sz, nx, ny, nz, nc, x, y, z ;

  if (FZERO(l_convex))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    vertex = &mris->vertices[vno] ;
    if (vertex->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    nx = vertex->nx ;
    ny = vertex->ny ;
    nz = vertex->nz ;
    x = vertex->x ;
    y = vertex->y ;
    z = vertex->z ;

    sx = sy = sz = 0.0 ;
    n=0;
    for (m = 0 ; m < vertex->vnum ; m++)
    {
      vn = &mris->vertices[vertex->v[m]] ;
      if (!vn->ripflag)
      {
        sx += vn->x - x;
        sy += vn->y - y;
        sz += vn->z - z;
        n++;
      }
    }
    if (n>0)
    {
      sx = sx/n;
      sy = sy/n;
      sz = sz/n;
    }
    nc = sx*nx+sy*ny+sz*nz;   /* projection onto normal */
    if (nc < 0)
      nc = 0 ;
    sx = nc*nx ;              /* move in normal direction */
    sy = nc*ny ;
    sz = nc*nz;

    vertex->dx += l_convex * sx ;
    vertex->dy += l_convex * sy ;
    vertex->dz += l_convex * sz ;
    if (vno == Gdiag_no)
      fprintf(stdout, "v %d convexity term: (%2.3f, %2.3f, %2.3f)\n",
              vno, l_convex*sx, l_convex*sy, l_convex*sz) ;
  }

  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
static int
mrisComputeNormalSpringTerm(MRI_SURFACE *mris, double l_spring)
{
  int     vno, n, m ;
  VERTEX  *vertex, *vn ;
  float   sx, sy, sz, nx, ny, nz, nc, x, y, z ;

  if (FZERO(l_spring))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    vertex = &mris->vertices[vno] ;
    if (vertex->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    nx = vertex->nx ;
    ny = vertex->ny ;
    nz = vertex->nz ;
    x = vertex->x ;
    y = vertex->y ;
    z = vertex->z ;

    sx = sy = sz = 0.0 ;
    n=0;
    for (m = 0 ; m < vertex->vnum ; m++)
    {
      vn = &mris->vertices[vertex->v[m]] ;
      if (!vn->ripflag)
      {
        sx += vn->x - x;
        sy += vn->y - y;
        sz += vn->z - z;
        n++;
      }
    }
    if (n>0)
    {
      sx = sx/n;
      sy = sy/n;
      sz = sz/n;
    }
    nc = sx*nx+sy*ny+sz*nz;   /* projection onto normal */
    sx = l_spring*nc*nx ;              /* move in normal direction */
    sy = l_spring*nc*ny ;
    sz = l_spring*nc*nz ;

    vertex->dx += sx ;
    vertex->dy += sy ;
    vertex->dz += sz ;
    if (vno == Gdiag_no)
      fprintf(stdout, "v %d spring normal term:  (%2.3f, %2.3f, %2.3f)\n",
              vno, sx, sy, sz) ;
  }


  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
static int
mrisComputeTangentialSpringTerm(MRI_SURFACE *mris, double l_spring)
{
  int     vno, n, m ;
  VERTEX  *v, *vn ;
  float   sx, sy, sz, x, y, z, nc ;

  if (FZERO(l_spring))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    if (v->border && !v->neg)
      continue ;

    x = v->x ;
    y = v->y ;
    z = v->z ;

    sx = sy = sz = 0.0 ;
    n=0;
    for (m = 0 ; m < v->vnum ; m++)
    {
      vn = &mris->vertices[v->v[m]] ;
      if (!vn->ripflag)
      {
        sx += vn->x - x;
        sy += vn->y - y;
        sz += vn->z - z;
        n++;
      }
    }
#if 0
    n = 4 ;  /* avg # of nearest neighbors */
#endif
    if (n>0)
    {
      sx = sx/n;
      sy = sy/n;
      sz = sz/n;
    }

    nc = sx*v->nx+sy*v->ny+sz*v->nz;   /* projection onto normal */
    sx = l_spring*(sx - nc*v->nx);     /* remove  normal component
                                                    and then scale */
    sy = l_spring*(sy - nc*v->ny);
    sz = l_spring*(sz - nc*v->nz);

    v->dx += sx ;
    v->dy += sy ;
    v->dz += sz ;
    if (vno == Gdiag_no)
      printf("v %d spring tangent term: (%2.3f, %2.3f, %2.3f)\n",
             vno, sx, sy, sz) ;
  }


  return(NO_ERROR) ;
}

static double
mrisComputeDuraError(MRI_SURFACE *mris, INTEGRATION_PARMS *parms)
{
  double dura_thresh = parms->dura_thresh, sse ;
  MRI *mri_dura = parms->mri_dura ;
  int     vno ;
  VERTEX  *v ;
  float   x, y, z ;
  double    val0, xw, yw, zw, delV ;

  if (FZERO(parms->l_dura))
    return(0.0) ;

  for (sse = 0.0, vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag || v->val < 0)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    x = v->x ;
    y = v->y ;
    z = v->z ;

    MRISvertexToVoxel(mris, v, mri_dura, &xw, &yw, &zw) ;
    MRIsampleVolume(mri_dura, xw, yw, zw, &val0) ;
    if (val0 < dura_thresh)
      continue ; // no effect

    delV = dura_thresh - val0 ;
    sse += delV*delV ;
  }

  return(sse) ;
}


static int
mrisComputeDuraTerm(MRI_SURFACE *mris,
                    double l_dura,
                    MRI *mri_dura,
                    double dura_thresh)
{
  int     vno ;
  VERTEX  *v ;
  float   x, y, z, nx, ny, nz, dx, dy, dz ;
  double    val0, xw, yw, zw, del, delI, delV ;

  if (FZERO(l_dura))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag || v->val < 0)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    x = v->x ;
    y = v->y ;
    z = v->z ;

    MRISvertexToVoxel(mris, v, mri_dura, &xw, &yw, &zw) ;
    MRIsampleVolume(mri_dura, xw, yw, zw, &val0) ;
    if (val0 < dura_thresh)
      continue ; // no effect

    nx = v->nx ;
    ny = v->ny ;
    nz = v->nz ;

    delV = dura_thresh - val0 ;
    delI = 1 ;

    del = l_dura * delV * delI ;

    dx = nx * del ;
    dy = ny * del ;
    dz = nz * del ;

    v->dx += dx ;
    v->dy += dy ;
    v->dz += dz ;

    if (vno == Gdiag_no)
    {
      double xwi, ywi, zwi, xwo, ywo, zwo, val_inside, val_outside ;

      x = v->x ;
      y = v->y ;
      z = v->z ;

      /* sample outward from surface */
      xw = x + nx ;
      yw = y + ny ;
      zw = z + nz ;
      MRISsurfaceRASToVoxel(mris, mri_dura, xw, yw, zw, &xwo, &ywo, &zwo);
      MRIsampleVolume(mri_dura, xw, yw, zw, &val_outside) ;

      /* sample inward from surface */
      xw = x - nx ;
      yw = y - ny ;
      zw = z - nz ;
      MRISsurfaceRASToVoxel(mris, mri_dura, xw, yw, zw, &xwi, &ywi, &zwi);
      MRIsampleVolume(mri_dura, xw, yw, zw, &val_inside) ;

      MRISsurfaceRASToVoxel(mris, mri_dura, x, y, z, &xw, &yw, &zw);
      fprintf(stdout,
              "D(%2.1f,%2.1f,%2.1f)=%2.1f, Do(%2.1f,%2.1f,%2.1f)=%2.1f, "
              "Di(%2.1f,%2.1f,%2.1f)=%2.1f\n",
              xw,yw,zw,val0, xwo, ywo,zwo,
              val_outside,xwi,ywi,zwi,val_inside);
      fprintf(stdout, "v %d dura term:      (%2.3f, %2.3f, %2.3f), "
              "delV=%2.1f, delI=%2.0f\n", vno, dx, dy, dz, delV, delI) ;
    }
  }


  return(NO_ERROR) ;
}
/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
#define NORMAL_MOVEMENT   0.1
#define NSAMPLES          15
#define SAMPLE_DISTANCE   0.1

static int
mrisComputeIntensityTerm(MRI_SURFACE *mris, double l_intensity, MRI *mri_brain,
                         MRI *mri_smooth, double sigma_global,
                         INTEGRATION_PARMS *parms)
{
  int     vno ;
  VERTEX  *v ;
  float   x, y, z, nx, ny, nz, dx, dy, dz ;
  double    val0, xw, yw, zw, del, val_outside, val_inside, delI, delV, k,
          ktotal_outside, xvi, yvi, zvi, interior, ktotal_inside ;
  double  sigma ;
  MRI     *mri_interior ;


  if (FZERO(l_intensity))
    return(NO_ERROR) ;

  if (parms->grad_dir == 0 && parms->fill_interior)  // create binary mask of interior of surface
  {
    mri_interior = MRISfillInterior(mris, 0.5, NULL) ;
    if (Gdiag & DIAG_WRITE && DIAG_VERBOSE_ON)
      MRIwrite(mri_interior, "int.mgz") ;
  }
  else
    mri_interior = NULL ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag || v->val < 0)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    x = v->x ; y = v->y ; z = v->z ;

    MRISvertexToVoxel(mris, v, mri_brain, &xw, &yw, &zw) ;
    MRIsampleVolume(mri_brain, xw, yw, zw, &val0) ;
    sigma = v->val2 ;
    if (FZERO(sigma))
      sigma = sigma_global ;
    if (FZERO(sigma))
      sigma = 0.25 ;

    nx = v->nx ; ny = v->ny ; nz = v->nz ;

    /* compute intensity gradient using smoothed volume */
    if (parms->grad_dir == 0)
    {
      double dist, val, step_size ;
      int  n ;

      step_size = MIN(sigma/2,
                      MIN(mri_brain->xsize,
                          MIN(mri_brain->ysize, mri_brain->zsize))*0.5) ;
      ktotal_inside = ktotal_outside = 0.0 ;
      for (n = 0, val_outside = val_inside = 0.0, dist = step_size ;
           dist <= 2*sigma;
           dist += step_size, n++)
      {
        k = exp(-dist*dist/(2*sigma*sigma)) ;
        xw = x + dist*nx ; yw = y + dist*ny ; zw = z + dist*nz ;
        if (mri_interior)
        {
          MRISsurfaceRASToVoxelCached(mris, mri_interior, xw, yw, zw, &xvi,&yvi,&zvi) ;
          MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
        }

        if (mri_interior == NULL || interior < .9)
        {
          ktotal_outside += k ;
          MRISsurfaceRASToVoxelCached(mris, mri_brain, xw, yw, zw, &xw, &yw, &zw) ;
          MRIsampleVolume(mri_brain, xw, yw, zw, &val) ;
          val_outside += k*val ;
        }
        else
          DiagBreak() ;

        xw = x - dist*nx ; yw = y - dist*ny ; zw = z - dist*nz ;
        if (mri_interior)
        {
          MRISsurfaceRASToVoxelCached(mris, mri_interior,xw,yw,zw, &xvi,&yvi,&zvi);
          MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
        }

        if (mri_interior == NULL || interior > 0)
        {
          MRISsurfaceRASToVoxelCached(mris, mri_brain, xw, yw, zw,&xw,&yw,&zw);
          MRIsampleVolume(mri_brain, xw, yw, zw, &val) ;
          val_inside += k*val ;
          ktotal_inside += k ;
        }
        else
          DiagBreak() ;
      }
      if (ktotal_inside> 0)
        val_inside /= (double)ktotal_inside ;
      if (ktotal_outside> 0)
        val_outside /= (double)ktotal_outside ;
    }
    else  // don't compute gradient - assume
    {
      val_outside = parms->grad_dir ;
      val_inside = -parms->grad_dir ;
    }

    delV = v->val - val0 ;
    delI = (val_outside - val_inside) / 2.0 ;

    if (!FZERO(delI))
      delI /= fabs(delI) ;
    else
      delI = -1 ;   /* intensities tend to increase inwards */

    if (delV > 5)
      delV = 5 ;
    else if (delV < -5)
      delV = -5 ;

    del = l_intensity * delV * delI ;

    dx = nx * del ;
    dy = ny * del ;
    dz = nz * del ;

    v->dx += dx ;
    v->dy += dy ;
    v->dz += dz ;


    if (vno == Gdiag_no)
    {
      double xwi, ywi, zwi, xwo, ywo, zwo ;

      x = v->x ; y = v->y ;z = v->z ;

      /* sample outward from surface */
      xw = x + nx ; yw = y + ny ; zw = z + nz ;
      MRISsurfaceRASToVoxelCached(mris, mri_smooth, xw, yw, zw, &xwo, &ywo, &zwo) ;
      /* sample inward from surface */
      xw = x - nx ; yw = y - ny ; zw = z - nz ;
      MRISsurfaceRASToVoxelCached(mris, mri_smooth, xw, yw, zw, &xwi, &ywi, &zwi) ;
      MRISsurfaceRASToVoxelCached(mris, mri_smooth, x, y, z, &xw, &yw, &zw) ;
      fprintf(stdout,
              "I(%2.1f,%2.1f,%2.1f)=%2.1f, Io(%2.1f,%2.1f,%2.1f)=%2.1f, "
              "Ii(%2.1f,%2.1f,%2.1f)=%2.1f\n",
              xw,yw,zw,val0, xwo, ywo,zwo,
              val_outside,xwi,ywi,zwi,val_inside);
      fprintf(stdout, "v %d intensity term:      (%2.3f, %2.3f, %2.3f), "
              "delV=%2.1f, delI=%2.0f\n", vno, dx, dy, dz, delV, delI) ;
    }
  }

  if (mri_interior)
    MRIfree(&mri_interior) ;
  return(NO_ERROR) ;
}
static int
mrisComputeTargetLocationTerm(MRI_SURFACE *mris, double l_location, INTEGRATION_PARMS *parms)
{
  int     vno ;
  VERTEX  *v ;
  double    dx, dy, dz, norm ;


  if (FZERO(l_location))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag || v->val < 0)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    dx = v->targx - v->x ; dy = v->targy-v->y ; dz = v->targz-v->z ;

    if (vno == Gdiag_no)
    {
      fprintf(stdout,
              "targ: (%2.1f, %2.1f, %2.f), current (%2.1f, %2.1f, %2.1f), del (%2.1f, %2.1f, %2.1f), dot=%2.3f\n",
              v->targx, v->targy, v->targz, v->x, v->y, v->z, dx, dy, dz,
              dx*v->nx+dy*v->ny+dz*v->nz) ;
    }
    norm = sqrt(dx*dx + dy*dy + dz*dz) ;
#define LOCATION_MOVE_LEN  0.1
    if (norm > LOCATION_MOVE_LEN) // so things move at the same speed
    {
      dx /= norm ; dy /= norm ; dz /= norm ;
      dx *= LOCATION_MOVE_LEN ;
      dy *= LOCATION_MOVE_LEN ;
      dz *= LOCATION_MOVE_LEN ;
    }

    if (!devFinite(dx) || !devFinite(dy) || !devFinite(dz))
      DiagBreak() ;
    v->dx += l_location * dx ;
    v->dy += l_location * dy ;
    v->dz += l_location * dz ;

  }

  return(NO_ERROR) ;
}


static int
mrisComputeIntensityTerm_mef(MRI_SURFACE *mris, double l_intensity,
                             MRI *mri_30,
                             MRI *mri_5,
                             double sigma_global,
                             float weight30, float weight5,
                             INTEGRATION_PARMS *parms)
{
  int     vno ;
  VERTEX  *v ;
  float   x, y, z, nx, ny, nz, dx, dy, dz ;
  double    val0, xw, yw, zw, del, val_outside, val_inside, delI, delV, k,
          ktotal_outside, ktotal_inside, interior, xvi, yvi, zvi ;
  double  sigma ;
  MRI     *mri_interior ;

  if (FZERO(l_intensity))
    return(NO_ERROR) ;

  if (parms->grad_dir == 0 && parms->fill_interior)  // create binary mask of interior of surface
  {
    mri_interior = MRISfillInterior(mris, 0.5, NULL) ;
    if (Gdiag & DIAG_WRITE && DIAG_VERBOSE_ON)
      MRIwrite(mri_interior, "int.mgz") ;
  }
  else
    mri_interior = NULL ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag || v->val < 0)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    x = v->x ; y = v->y ; z = v->z ;
    nx = v->nx ; ny = v->ny ; nz = v->nz ;

    sigma = v->val2 ;
    if (FZERO(sigma))
      sigma = sigma_global ;
    if (!FZERO(weight30))
    {
      MRISsurfaceRASToVoxelCached(mris, mri_30, x, y, z, &xw, &yw, &zw) ;
      MRIsampleVolume(mri_30, xw, yw, zw, &val0) ;

      /* compute intensity gradient using smoothed volume */

      if (parms->grad_dir == 0)
      {
        double dist, val, step_size ;
        int  n ;

        step_size = MIN(sigma/2,
                        MIN(mri_30->xsize,
                            MIN(mri_30->ysize, mri_30->zsize))*0.5) ;
        ktotal_outside = ktotal_inside = 0.0 ;
        for (n = 0, val_outside = val_inside = 0.0, dist = step_size ;
             dist <= 2*sigma;
             dist += step_size, n++)
        {
          k = exp(-dist*dist/(2*sigma*sigma)) ;
          xw = x + dist*nx ; yw = y + dist*ny ; zw = z + dist*nz ;
          if (mri_interior)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_interior, xw, yw, zw, &xvi,&yvi,&zvi) ;
            MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
          }

          if (mri_interior == NULL || interior < .9)
          {
            MRIsampleVolume(mri_30, xw, yw, zw, &val) ;
            val_outside += k*val ;
            ktotal_outside += k ;
          }
          else
            DiagBreak() ;

          xw = x - dist*nx ; yw = y - dist*ny ; zw = z - dist*nz ;
          if (mri_interior)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_interior, xw, yw, zw, &xvi,&yvi,&zvi) ;
            MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
          }
          if (mri_interior == NULL || interior > 0)
          {
            MRIsampleVolume(mri_30, xw, yw, zw, &val) ;
            val_inside += k*val ;
            ktotal_inside += k ;
          }
          else
            DiagBreak() ;
        }
        if (ktotal_inside> 0)
          val_inside /= (double)ktotal_inside ;
        if (ktotal_outside> 0)
          val_outside /= (double)ktotal_outside ;
      }
      else  // don't compute gradient - assume
      {
        val_outside = parms->grad_dir ;
        val_inside = -parms->grad_dir ;
      }

      delV = v->val - val0 ;
      delI = (val_outside - val_inside) / 2.0 ;

      if (!FZERO(delI))
        delI /= fabs(delI) ;
      else
        delI = -1; //intensity tends to increase inwards for flash30

      if (delV > 5)
        delV = 5 ;
      else if (delV < -5)
        delV = -5 ;

      del = l_intensity * delV * delI ;

      dx = nx * del*weight30 ; dy = ny * del*weight30 ; dz = nz * del*weight30;

      if (dx*nx+dy*ny+dz*nz < 0)
        DiagBreak() ;

      if (vno == Gdiag_no)
      {
        double xwi, ywi, zwi, xwo, ywo, zwo ;

        x = v->x ; y = v->y ;z = v->z ;

        /* sample outward from surface */
        xw = x + nx ; yw = y + ny ; zw = z + nz ;
        MRISsurfaceRASToVoxelCached(mris, mri_30, xw, yw, zw, &xwo, &ywo, &zwo) ;
        /* sample inward from surface */
        xw = x - nx ; yw = y - ny ; zw = z - nz ;
        MRISsurfaceRASToVoxelCached(mris, mri_30, xw, yw, zw, &xwi, &ywi, &zwi) ;
        fprintf(stdout,
                "I30(%2.1f,%2.1f,%2.1f)=%2.1f, Io(%2.1f,%2.1f,%2.1f)=%2.1f, "
                "Ii(%2.1f,%2.1f,%2.1f)=%2.1f\n",
                xw,yw,zw,val0, xwo, ywo,zwo,
                val_outside,xwi,ywi,zwi,val_inside);
        fprintf(stdout, "v %d I30 intensity term:  (%2.3f, %2.3f, %2.3f), "
                "targ=%2.1f, delV=%2.1f, delI=%2.0f, dot=%2.1f\n", vno, dx, dy, dz, v->val,
                delV, delI,
                dx*nx+dy*ny+dz*nz) ;
      }
      v->dx += dx ; v->dy += dy ; v->dz += dz ;
    }

    //now compute flash5
    /* compute intensity gradient using smoothed volume */
    if (!FZERO(weight5))
    {
      MRISsurfaceRASToVoxelCached(mris, mri_5, x, y, z, &xw, &yw, &zw);
      MRIsampleVolume(mri_5, xw, yw, zw, &val0) ;
      if (parms->grad_dir == 0)
      {
        double dist, val, step_size ;
        int  n ;

        step_size = MIN(sigma/2,
                        MIN(mri_5->xsize,
                            MIN(mri_5->ysize, mri_5->zsize))*0.5) ;
        ktotal_inside = ktotal_outside = 0.0 ;
        for (n = 0, val_outside = val_inside = 0.0, dist = step_size ;
             dist <= 2*sigma;
             dist += step_size, n++)
        {
          k = exp(-dist*dist/(2*sigma*sigma)) ;

          xw = x + dist*nx ; yw = y + dist*ny ; zw = z + dist*nz ;
          if (mri_interior)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_interior, xw, yw, zw,
                                        &xvi,&yvi,&zvi) ;
            MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
          }
          if (mri_interior == NULL || interior < .9)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_5, xw, yw, zw, &xw, &yw, &zw);
            MRIsampleVolume(mri_5, xw, yw, zw, &val) ;
            val_outside += k*val ;
            ktotal_outside += k ;
          }

          xw = x - dist*nx ; yw = y - dist*ny ; zw = z - dist*nz ;
          if (mri_interior)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_interior, xw, yw, zw,
                                        &xvi,&yvi,&zvi) ;
            MRIsampleVolume(mri_interior, xvi, yvi, zvi, &interior) ;
          }
          if (mri_interior == NULL || interior > 0)
          {
            MRISsurfaceRASToVoxelCached(mris, mri_5, xw, yw, zw, &xw, &yw, &zw);
            MRIsampleVolume(mri_5, xw, yw, zw, &val) ;
            val_inside += k*val ;
            ktotal_inside += k ;
          }
        }
        if (ktotal_inside> 0)
          val_inside /= (double)ktotal_inside ;
        if (ktotal_outside> 0)
          val_outside /= (double)ktotal_outside ;
      }
      else  // don't compute gradient - assume
      {
        val_outside = parms->grad_dir ;
        val_inside = -parms->grad_dir ;
      }

      delV = v->valbak - val0 ;
      delI = (val_outside - val_inside) / 2.0 ;

      if (!FZERO(delI))
        delI /= fabs(delI) ;
      else
        delI = 0 ;

      if (delV > 5)
        delV = 5 ;
      else if (delV < -5)
        delV = -5 ;

      del = l_intensity * delV * delI ;

      dx = nx * del*weight5 ; dy = ny * del*weight5; dz = nz * del*weight5;

      if (vno == Gdiag_no && !FZERO(weight5))
      {
        double xwi, ywi, zwi, xwo, ywo, zwo ;

        x = v->x ; y = v->y ;z = v->z ;

        /* sample outward from surface */
        xw = x + nx ; yw = y + ny ; zw = z + nz ;
        MRISsurfaceRASToVoxelCached(mris, mri_30, xw, yw, zw, &xwo, &ywo, &zwo) ;
        /* sample inward from surface */
        xw = x - nx ; yw = y - ny ; zw = z - nz ;
        MRISsurfaceRASToVoxelCached(mris, mri_30, xw, yw, zw, &xwi, &ywi, &zwi) ;
        MRISsurfaceRASToVoxelCached(mris, mri_30, x, y, z, &xw, &yw, &zw) ;
        fprintf(stdout,
                "I5(%2.1f,%2.1f,%2.1f)=%2.1f, Io(%2.1f,%2.1f,%2.1f)=%2.1f, "
                "Ii(%2.1f,%2.1f,%2.1f)=%2.1f\n",
                xw,yw,zw,val0, xwo, ywo,zwo,
                val_outside,xwi,ywi,zwi,val_inside);
        fprintf(stdout, "v %d I5 intensity term:   (%2.3f, %2.3f, %2.3f), "
                "delV=%2.1f, delI=%2.0f, dot=%2.1f\n",
                vno, dx, dy, dz, delV, delI,
                dx*nx + dy*ny + dz*nz) ;
      }
      v->dx += dx ; v->dy += dy ; v->dz += dz ; // add flash5 component
    }
  }


  if (mri_interior)
    MRIfree(&mri_interior) ;
  return(NO_ERROR) ;
}



