/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  Apply a uniform outward expansion force.
  ------------------------------------------------------*/
static int
mrisComputeSphereTerm(MRI_SURFACE *mris, double l_sphere, float radius)
{
  int     vno ;
  VERTEX  *v ;
  float   r, x, y, z, x0, y0, z0 ;

  if (FZERO(l_sphere))
    return(0.0f) ;

#if 1
  x0 = (mris->xlo+mris->xhi)/2.0f ;
  y0 = (mris->ylo+mris->yhi)/2.0f ;
  z0 = (mris->zlo+mris->zhi)/2.0f ;
#else
  x0 = mris->x0 ;
  y0 = mris->y0 ;
  z0 = mris->z0 ;
#endif

#if 0
  // make sure center is inside surface, otherwise move it
  {
    float dot, dx, dy, dz ;
    static int iter = 0 ;

    vno = MRISfindClosestVertex(mris, x0, y0, z0, &r) ;
    v = &mris->vertices[vno] ;
    dx = x0-v->x ;
    dy = y0-v->y ;
    dz = z0-v->z ;
    dot = v->nx*dx + v->ny*dy + v->nz*dz ;
    if (iter < 20 && dot > 0) // outside surface!
    {
      if (iter > 500)
        DiagBreak() ;
      printf("centroid (%2.1f, %2.1f, %2.1f) outside surface "
             "(dot = %2.1f, v %d = (%2.1f, %2.1f, %2.1f) n = "
             "(%2.1f, %2.1f, %2.1f)\n",
             x0, y0, z0, dot, vno, v->x, v->y, v->z, v->nx, v->ny, v->nz) ;
      x0 = v->x-0.5*v->nx ;
      y0 = v->y-(0.5*v->ny) ;
      z0 = v->z-(0.5*v->nz) ;
      mris->x0 = x0 ;
      mris->y0 = y0 ;
      mris->z0 = z0 ;
      print("moving centroid to (%2.1f, %2.1f, %2.1f)\n", x0, y0, z0) ;
      vno = MRISfindClosestVertex(mris, x0, y0, z0, &r) ;
      v = &mris->vertices[vno] ;
      dx = x0-v->x ;
      dy = y0-v->y ;
      dz = z0-v->z ;
      dot = v->nx*dx + v->ny*dy + v->nz*dz ;
      if (dot > 0)
        printf("still outside!!!!!\n") ;
    }
    iter++ ;
  }
#endif

  if (radius < 0)
  {
    radius = 0 ;
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      v = &mris->vertices[vno] ;
      if (v->ripflag)
        continue ;
      if (vno == Gdiag_no)
        DiagBreak() ;

      x = v->x-x0 ;
      y = v->y-y0 ;
      z = v->z-z0 ;
      r = sqrt(x*x+y*y+z*z) ;
      if (r > radius)
        radius = r ;
    }
    radius++ ;
  }

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    x = v->x-x0 ;
    y = v->y-y0 ;
    z = v->z-z0 ;
    r = sqrt(x*x+y*y+z*z) ;
    if (FZERO(r))
      continue ;
    x /= r ;
    y /= r ;
    z /= r ;  /* normal direction */
    //    x = v->nx ; y = v->ny ; z = v->nz ;
    r = (radius - r) / radius ;
    if (vno == Gdiag_no &&
        (r*l_sphere * x*v->nx +
         r*l_sphere * y*v->ny +
         r*l_sphere * z*v->nz < 0))
      DiagBreak() ;
    v->dx += r*l_sphere * x ;
    v->dy += r*l_sphere * y ;
    v->dz += r*l_sphere * z ;
    if (vno == Gdiag_no)
      fprintf(stdout, "v %d sphere   "
              " term: (%2.3f, %2.3f, %2.3f), r=%2.2f\n",
              vno, v->dx, v->dy, v->dz, r) ;
  }


  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  Apply a uniform outward expansion force.
  ------------------------------------------------------*/
static int
mrisComputeExpansionTerm(MRI_SURFACE *mris, double l_expand)
{
  int     vno ;
  VERTEX  *v ;

  if (FZERO(l_expand))
    return(0.0f) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    v->dx += l_expand * v->nx ;
    v->dy += l_expand * v->ny ;
    v->dz += l_expand * v->nz ;
    if (vno == Gdiag_no)
      printf("v %d expansion term: (%2.3f, %2.3f, %2.3f)\n",
             vno, l_expand*v->nx, l_expand*v->ny, l_expand*v->nz) ;
  }

  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  File Format is:

  name x y z
  ------------------------------------------------------*/
static int
mrisComputeSpringTerm(MRI_SURFACE *mris, double l_spring)
{
  int     vno, n, m ;
  VERTEX  *v, *vn ;
  float   sx, sy, sz, x, y, z, dist_scale ;

  if (FZERO(l_spring))
    return(NO_ERROR) ;

#if METRIC_SCALE
  if (mris->patch)
    dist_scale = 1.0 ;
  else
    dist_scale = sqrt(mris->orig_area / mris->total_area) ;
#else
  dist_scale = 1.0 ;
#endif
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    if (v->border && !v->neg)
      continue ;

    x = v->x ;
    y = v->y ;
    z = v->z ;

    sx = sy = sz = 0.0 ;
    n=0;
    for (m = 0 ; m < v->vnum ; m++)
    {
      vn = &mris->vertices[v->v[m]] ;
      if (!vn->ripflag)
      {
        sx += vn->x - x;
        sy += vn->y - y;
        sz += vn->z - z;
        n++;
      }
    }
#if 0
    n = 4 ;  /* avg # of nearest neighbors */
#endif
    if (n>0)
    {
      sx = dist_scale*sx/n;
      sy = dist_scale*sy/n;
      sz = dist_scale*sz/n;
    }

    sx *= l_spring ;
    sy *= l_spring ;
    sz *= l_spring ;
    v->dx += sx ;
    v->dy += sy ;
    v->dz += sz ;
    if (vno == Gdiag_no)
      fprintf(stdout, "v %d spring term:         (%2.3f, %2.3f, %2.3f)\n",
              vno, sx, sy, sz) ;
  }


  return(NO_ERROR) ;
}


/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description

  Note: this function assumes that the mris surface
  has the original (i.e. after
  global rotational alignment) spherical coordinates in the TMP2_VERTICES
  ------------------------------------------------------*/
static int
mrisComputeLaplacianTerm(MRI_SURFACE *mris, double l_lap)
{
  int     vno, n, m ;
  VERTEX  *v, *vn ;
  float   x, y, z, vx, vy, vz, vnx, vny, vnz, dx, dy, dz ;

  if (FZERO(l_lap))
    return(NO_ERROR) ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    if (vno == Gdiag_no)
      DiagBreak() ;

    if (v->border && !v->neg)
      continue ;

    x = v->x ;
    y = v->y ;
    z = v->z ;

    n=0;
    vx = v->x - v->tx2 ;
    vy = v->y - v->ty2 ;
    vz = v->z - v->tz2 ;
    dx = dy = dz = 0.0f ;
    for (m = 0 ; m < v->vnum ; m++)
    {
      vn = &mris->vertices[v->v[m]] ;
      if (!vn->ripflag)
      {
        vnx = vn->x - vn->tx2 ;
        vny = vn->y - vn->ty2 ;
        vnz = vn->z - vn->tz2 ;
        dx += (vnx-vx) ;
        dy += (vny-vy) ;
        dz += (vnz-vz) ;
        if ((x == Gx && y == Gy && z == Gz) &&
            (Gdiag & DIAG_SHOW) && DIAG_VERBOSE_ON)
          printf("\tvertex %d: V=(%2.2f,%2.2f,%2.2f), "
                 "DX=(%2.2f,%2.2f,%2.2f)\n",
                 vno, vnx, vny, vnz, vnx-vx, vny-vy, vnz-vz) ;
        n++;
      }
    }
    if (n>0)
    {
      dx = dx * l_lap / n ;
      dy = dy * l_lap / n ;
      dz = dz * l_lap / n ;
    }

    v->dx += dx ;
    v->dy += dy ;
    v->dz += dz ;
    if (vno == Gdiag_no)
      printf("l_lap: v %d: DX=(%2.2f,%2.2f,%2.2f)\n", vno, dx, dy, dz) ;
  }

  return(NO_ERROR) ;
}


