        v->cy = v->cy ;
        v->cz = v->cz ;
        break ;
      case ORIGINAL_VERTICES:
        v->cx = v->origx ;
        v->cy = v->origy ;
        v->cz = v->origz ;
        break ;
      case TMP2_VERTICES:
        v->cx = v->tx2 ;
