      vp1 =
        MRISPfunctionVal(parms->mrisp_template, mris, x+vx, y+vy, z+vz, fno);
      vm1 =
        MRISPfunctionVal(parms->mrisp_template, mris, x-vx, y-vy, z-vz, fno);
      du = (up1 - um1) / (2 * D_DIST) ;
      dv = (vp1 - vm1) / (2 * D_DIST) ;
      v->dx -= coef * (du*e1x + dv*e2x) ;
      v->dy -= coef * (du*e1y + dv*e2y) ;
