/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
int
MRISgaussianFilterD(MRI_SURFACE *mris, double wt)
{
  int    vno, vnb, *pnb, vnum ;
  double nbr_wt, val, norm ;
  VERTEX *v, *vn ;

  nbr_wt = 1-wt ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    pnb = v->v ;
    vnum = v->vnum ;
    
    val = wt * v->d ;
    
    for ( norm = wt, vnb = 0 ; vnb < vnum ; vnb++)
    {
      vn = &mris->vertices[*pnb++]; /* neighboring vertex pointer */
      if (vn->ripflag)
        continue ;
      
      val += nbr_wt*vn->d ;
      norm += nbr_wt ;
    }
    v->tdx = val/norm ;
  }
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    v->d = v->tdx ;
  }
  return(NO_ERROR) ;
}
