    if (v->ripflag)
      continue ;
    if (fabs(v->k1) > fabs(v->k2))
      v->curv = v->k2 ;
    else
      v->curv = v->k1 ;
    /*    v->curv = MIN(v->k1, v->k2) ;*/
    if (v->curv > kmax)
      kmax = v->curv ;
    if (v->curv < kmin)
      kmin = v->curv ;
