/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
int
MRISuseCurvatureStretch(MRI_SURFACE *mris)
{
  int    vno, n ;
  VERTEX *v ;
  float   kmin, kmax, dist, dist_orig, curv, dist_scale, max_stretch, stretch ;

  dist_scale = sqrt(mris->orig_area / mris->total_area) ;
  kmin = 100000.0f ;
  kmax = -100000.0f ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    max_stretch = 0.0f ;
    for (curv = 0.0f, n = 0 ; n < v->vtotal ; n++)
    {
      dist = dist_scale * v->dist[n] ;
      dist_orig = v->dist_orig[n] ;
      stretch = dist - dist_orig ;
      if (stretch > max_stretch)
        max_stretch = stretch ;
    }
    v->curv = max_stretch ;
    if (v->curv > kmax)
      kmax = v->curv ;
    if (v->curv < kmin)
      kmin = v->curv ;
    if (v->curv < 0)
      DiagBreak() ;
  }

  mris->min_curv = kmin ;
  mris->max_curv = kmax ;
  return(NO_ERROR) ;
}
/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/
int
MRISuseNegCurvature(MRI_SURFACE *mris)
{
  int    vno, fno ;
  VERTEX *v ;
  FACE   *f ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    v->curv = 0 ;
    for (fno = 0 ; fno < v->num ; fno++)
    {
      f = &mris->faces[v->f[fno]] ;
      if (f->area < 0.0f)
        v->curv = 1.0f ;
    }
  }

  mris->min_curv = 0 ;
  mris->max_curv = 1 ;
  return(NO_ERROR) ;
}
