/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  ------------------------------------------------------*/

int
MRISmarkRandomVertices(MRI_SURFACE *mris, float prob_marked)
{
  int    vno ;
  VERTEX *v ;
  float  r ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    r = randomNumber(0.0, 1.0) ;
    if (r < prob_marked)
      v->marked = 1 ;
  }
  return(NO_ERROR) ;
}
