/*-----------------------------------------------------
  Parameters:

  Returns value:

  Description
  Set the positions of all vertices in a defect to the
  original, canonical, or tmp vertices.
  ------------------------------------------------------*/
static int
mrisRestoreDefectPositions(MRI_SURFACE *mris, DEFECT *defect, int which)
{
  int     vno, i ;
  VERTEX  *v ;

  for (i = 0 ; i < defect->nvertices ; i++)
  {
    vno = defect->vertices[i] ;
    v = &mris->vertices[vno] ;
#if 0
    if (v->ripflag)
      continue ;
#endif
    switch (which)
    {
    case CANONICAL_VERTICES:
      v->x = v->cx ;
      v->y = v->cy ;
      v->z = v->cz ;
      break ;
    case ORIGINAL_VERTICES:
      v->x = v->origx ;
      v->y = v->origy ;
      v->z = v->origz ;
      break ;
    case TMP2_VERTICES:
      v->x = v->tx2 ;
      v->y = v->ty2 ;
      v->z = v->tz2 ;
      break ;
    default:
    case TMP_VERTICES:
      v->x = v->tx ;
      v->y = v->ty ;
      v->z = v->tz ;
      break ;
    }
  }
  return(NO_ERROR) ;
}
