      vno1 = f->v[n1] ;
      f->v[n0] = vno1 ;
      f->v[n1] = vno0 ;
      mrisSetVertexFaceIndex(mris, vno0, fno) ;
      mrisSetVertexFaceIndex(mris, vno1, fno) ;
