      v->odx = delta_t * v->dx + momentum*v->odx ;
      v->ody = delta_t * v->dy + momentum*v->ody ;
      v->odz = delta_t * v->dz + momentum*v->odz ;
      mag = sqrt(v->odx*v->odx + v->ody*v->ody + v->odz*v->odz) ;
