        vn->marked = 1 ;
        wt = vn->nx*nx + vn->ny*ny + vn->nz*nz ;
        if (wt < 0) wt = 0 ;
        vlist[nbrs] = v->v[n] ;
        wts[nbrs] = exp(-1.0/(2.0f*sigma*sigma))*wt ;
