int
MRIScomputeSurfaceNormals(MRI_SURFACE *mris, int which, int navgs)
{
  int     vno, n, i ;
  VERTEX  *v, *vn ;
  double  nx, ny, nz, norm ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    MRIScomputeNormal(mris, which, vno, &nx, &ny, &nz) ;
    switch (which)
    {
    case CURRENT_VERTICES:   v->nx = nx ;  v->ny = ny ;  v->nz = nz ; break ;
    case WHITE_VERTICES:     v->wnx = nx ; v->wny = ny ; v->wnz = nz ; break ;
    case PIAL_VERTICES:      v->pnx = nx ; v->pny = ny ; v->pnz = nz ; break ;
    case ORIGINAL_VERTICES:  v->onx = nx ; v->ony = ny ; v->onz = nz ; break ;
    }
  }
  for (i = 0 ; i < navgs ; i++)
  {
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      v = &mris->vertices[vno] ;
      if (v->ripflag)
        continue ;
      switch (which)
      {
      default:
      case CURRENT_VERTICES:   nx = v->nx ;  ny = v->ny ;  nz = v->nz ; break ;
      case WHITE_VERTICES:     nx = v->wnx ; ny = v->wny ; nz = v->wnz ; break ;
      case PIAL_VERTICES:      nx = v->pnx ; ny = v->pny ; nz = v->pnz ; break ;
      case ORIGINAL_VERTICES:  nx = v->onx ; ny = v->ony ; nz = v->onz ; break ;
      }
      v->tdx = nx ; v->tdy = ny ; v->tdz = nz ;
      for (n = 0 ; n < v->vnum ; n++)
      {
        vn = &mris->vertices[v->v[n]] ;
        if (vn->ripflag)
          continue ;
        switch (which)
        {
        default:
        case CURRENT_VERTICES:   nx = vn->nx ;  ny = vn->ny ;  nz = vn->nz ; break ;
        case WHITE_VERTICES:     nx = vn->wnx ; ny = vn->wny ; nz = vn->wnz ; break ;
        case PIAL_VERTICES:      nx = vn->pnx ; ny = vn->pny ; nz = vn->pnz ; break ;
        case ORIGINAL_VERTICES:  nx = vn->onx ; ny = vn->ony ; nz = vn->onz ; break ;
        }
        v->tdx += nx ;  v->tdy += ny ;  v->tdz += nz ;
      }
      norm = sqrt(v->tdx * v->tdx + v->tdy * v->tdy + v->tdz*v->tdz) ;
      if (!FZERO(norm))
      {
        v->tdx /= norm ;
        v->tdy /= norm ;
        v->tdz /= norm ;
      }
    }
    for (vno = 0 ; vno < mris->nvertices ; vno++)
    {
      v = &mris->vertices[vno] ;
      if (v->ripflag)
        continue ;
      switch (which)
      {
      default:
      case CURRENT_VERTICES:   v->nx = v->tdx ;  v->ny = v->tdy ;  v->nz = v->tdz ; break ;
      case WHITE_VERTICES:     v->wnx = v->tdx ; v->wny = v->tdy ; v->wnz = v->tdz ; break ;
      case PIAL_VERTICES:      v->pnx = v->tdx ; v->pny = v->tdy ; v->pnz = v->tdz ; break ;
      case ORIGINAL_VERTICES:  v->onx = v->tdx ; v->ony = v->tdy ; v->onz = v->tdz ; break ;
      }
    }
  }

  return(NO_ERROR) ;
}

static double
mrisRmsDistanceError(MRI_SURFACE *mris)
{
  INTEGRATION_PARMS parms;
  double rms ;

  memset(&parms, 0, sizeof(parms)) ;
  parms.l_location = 1 ;
  rms = mrisComputeTargetLocationError(mris, &parms);
  return(sqrt(rms / MRISvalidVertices(mris))) ;
}
int
MRISscaleVertexCoordinates(MRI_SURFACE *mris, double scale)
{
  VERTEX  *v ;
  int     vno ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    v->x *= scale ; v->y *= scale ; v->z *= scale ;
  }
  return(NO_ERROR) ;
}
int
MRISstoreTangentPlanes(MRI_SURFACE *mris, int which_vertices)
{
  VERTEX  *v ;
  int     vno ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    v->pe1x = v->e1x ;  v->pe1y = v->e1y ;  v->pe1z = v->e1z ; 
    v->pe2x = v->e2x ;  v->pe2y = v->e2y ;  v->pe2z = v->e2z ; 
  }
  return(NO_ERROR) ;
}
int
MRISrepositionSurface(MRI_SURFACE *mris, MRI *mri, int *target_vnos, float *target_vals, int nv, int nsize, double sigma) 
{
  int               vno, n ;
  INTEGRATION_PARMS parms ;
  VERTEX            *v ;

  memset(&parms, 0, sizeof(parms)) ;
  parms.fill_interior = 0 ; 
  parms.projection = NO_PROJECTION ;
  parms.tol = 1e-4 ;
  parms.dt = 0.5f ;
  parms.base_dt = .1 ;
  parms.l_spring = 1.0f ;
  parms.l_curv = 1.0 ;
  parms.l_intensity = 0.2 ;
  parms.l_spring = 0.0f ;
  parms.l_curv = 1.0 ;
  parms.l_intensity = 0.2 ;
  parms.l_tspring = 1.0f ;
  parms.l_nspring = 0.5f ;

  parms.niterations = 0 ;
  parms.write_iterations = 0 /*WRITE_ITERATIONS */;
  parms.integration_type = INTEGRATE_MOMENTUM ;
  parms.momentum = 0.0 /*0.8*/ ;
  parms.dt_increase = 1.0 /* DT_INCREASE */;
  parms.dt_decrease = 0.50 /* DT_DECREASE*/ ;
  parms.error_ratio = 50.0 /*ERROR_RATIO */;
  /*  parms.integration_type = INTEGRATE_LINE_MINIMIZE ;*/
  parms.l_surf_repulse = 0.0 ;
  parms.l_repulse = 5 ;
  parms.niterations = 100 ;
  sprintf(parms.base_name, "nudge") ;

  for (vno = 0 ; vno < mris->nvertices ; vno++)
    mris->vertices[vno].ripflag = 1 ;
  for (n = 0 ; n < nv ; n++)
    mris->vertices[target_vnos[n]].ripflag = 0 ;
  MRISerodeRipped(mris, nsize) ;
  for (vno = 0 ; vno < mris->nvertices ; vno++)
  {
    v = &mris->vertices[vno] ;
    if (v->ripflag)
      continue ;
    v->val = target_vals[0] ;
    v->val2 = sigma ;
    v->marked = 1 ;
  }

  MRISpositionSurface(mris, mri, mri, &parms) ;
  return(NO_ERROR) ;
}

